package lesson1;

public class class4 {
	// loop, while, do while

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int b = 90;
		while (b<=89) {
			System.out.println(b);
			b++;
		}
		System.out.println("---------------------");
		int a = 90;
		do
		 {
			System.out.println(a);
			a++;
		}while (a<=89);
		
}
}